import { Component, Input, OnInit } from "@angular/core";
import { FaceSnape } from "../models/face-snap-models";

@Component({
  selector: "app-face-snap",
  templateUrl: "./face-snap.component.html",
  styleUrls: ["./face-snap.component.scss"],
})
export class FaceSnapComponent implements OnInit {
  @Input() faceSnap!: FaceSnape;

  title!: string;
  description!: string;
  createData!: Date;
  snaps!: number;
  imageUrl!: string;
  buttonText!: string;

  ngOnInit() {
    this.title = "archid";
    this.description = "Mon meilleur ami depuis tout petit !";
    this.createData = new Date();
    this.snaps = 6;

    this.imageUrl =
      "https://cdn.pixabay.com/photo/2015/05/31/16/03/teddy-bear-792273_1280.jpg";
    this.buttonText != "click me";
  }

  onSnap() {
    if (this.buttonText === "Oh Snap!") {
      this.faceSnap.snaps++;
      this.buttonText = "Oops, unSnap!";
    } else {
      this.faceSnap.snaps--;
      this.buttonText = "Oh Snap!";
    }
  }
}
<div class="face-snap-card">
  <h2>{{ faceSnap.title }}</h2>
  <p>Mis en ligne le {{ faceSnap.createdDate }}</p>
  <img [src]="faceSnap.imageUrl" [alt]="faceSnap.title" />
  <p>{{ faceSnap.description }}</p>
  <p>
    <button (click)="onSnap()">{{ buttonText }}</button>
    ðŸ¤Œ {{ faceSnap.snaps }}
  </p>
</div>
